function (doc) {
    splitfields = [ "tags_t", "attr_t", "sbr_groups_t", "linked_t", "tags", "products" ];
    for (var j=0;j  < splitfields.length; j++) {
       var fieldname=splitfields[j];
       var f_s = doc.getFirstField(fieldname);
       if (f_s != null) {
           var v_s = f_s.value;
       } else {
           var v_s = null;
       }
       if (v_s != null) {
           doc.removeFields(fieldname);
           var sa = null;
           if (v_s.charAt(0)=="#") {
              sa=v_s.slice(1,v_s.length-1).split("##")
           } else {
              sa=v_s.split(",")
           }
           for (var i = 0; i < sa.length; i++) {
               var fn=fieldname
               fn.replace("_t","");
               doc.addField('attr_' + fn,sa[i]);
           }
       }
    }
    return doc;
}
